import styled from "styled-components";
import { useSelector } from 'react-redux'
export default function Categories({ setState = (id: any) => { return id } }) {
	const state = useSelector((store: any) => store.categories)
	console.log("ðŸš€ ~ file: index.tsx ~ line 8 ~ Cart ~ state", state)

	return (
		<>
			<Container>
				<Label>Choose a categoriy:</Label>
				{state?.map((item: any) => (
					<CategoryLinks onClick={() => { setState(item.id) }} key={item.id}>{item.name}</CategoryLinks>
				))}
			</Container>
		</>
	)
}

const Container = styled.div`
	background-color: aliceblue;
	height: 100vh;
	position: fixed;
	top: 0;
	padding: 0 20px 0 60px;
	display: flex;
	flex-direction: column;
	@media (max-width: 1000px) {
		display: none;
	}
`

const CategoryLinks = styled.button`
	border: none;	
	width: auto;
	text-decoration: none;
	margin: 20px 0;
	font-size: 22px;
	color: black;
	opacity: 0.6;
	cursor: pointer;
	:hover {
		opacity: 1;
	}
	:active {
		opacity: 1;
	}
`

const Label = styled.label`
	margin-top: 20px;
	font-size: 22px;
`
