import React from 'react'
import styled from 'styled-components'
import { useDispatch } from 'react-redux'
import { ADDTOCART } from '../../store/action'

interface Card {
    id?: number;
    name?: string;
    price?: number;
    description?: string;
    image?: string;
    count?: Number;
}

export default function Card(props: Card) {
    const { id, name, price, description, image, count } = props
    const dispatch = useDispatch()
    const delivery = {
        id,
        name,
        price,
        description,
        image,
        count: 1
    }
    return (
        <StyledContainer>
            <CardHeader>
                <h2>{name}</h2>
                <StyledImage>
                    <img src={image} alt="" />
                </StyledImage>
                <p>{description}</p>
                <p>${price}</p>
                <p>{count} pieces</p>
            </CardHeader>
            <ButtonContainer>
                {/* <Button disabled background="red" onClick={() => dispatch({ type: "DECREMENT", id })}>-</Button>
                <Button background="green" onClick={() => dispatch({ type: "INCREMENT", id })}>+</Button> */}
                <Button width={130} onClick={() => dispatch({ type: ADDTOCART, delivery })}>Add to Card</Button>
            </ButtonContainer>
        </StyledContainer>
    )
}

export const StyledContainer = styled.div`
    background-color: aliceblue;
    border-radius: 23px;
    padding: 20px;
    margin: 0 40px 40px;
    height: 480px;
    width: 280px;
`

const CardHeader = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
`

const StyledImage = styled.div`
    width: 100%;
    height: 250px;
    img {
        width: 100%;
        height: 100%;
    }
`

const ButtonContainer = styled.div`
    display: flex;
    width: 100%;
    justify-content: space-between;
    margin-top: 20px;
`

const Button = styled.button<{ background?: string; width?: number }>`
    width: ${({ width }) => width ? width : 60}px;
    height: 60px;
    border-radius: 10px;
    background: ${({ background }) => background ? background : "linear-gradient(270deg, rgb(250, 188, 126) 0%, rgb(255, 150, 45) 100%) repeat scroll right bottom"};
    cursor: pointer;
    font-size: 20px;
    margin: 0 auto;
    border: none;
    :hover {
        background: linear-gradient(270deg, rgb(255, 150, 45) 100%, rgb(250, 188, 126) 0%) repeat scroll right bottom;
    }
`
