        },
        {
            id: 8,
            name: "Gold8",
            price: "3000",
            description: "",
            image: img8,
            count: 10
        }
    ]
}

export default function reducer(state = globalState, action: any) {
    console.log(action)
    // let element = array.find(a => a.id === action.id)
    let table = [...state.array]
    let filteredArray = table.filter(a => a.id !== action.id)
    const incr: any = table.map(item => {
        if (item.id === action.id) return item.count + 1
        return { ...state, array: incr }
    })
    console.log("ðŸš€ ~ file: reducer.ts ~ line 85 ~ reducer ~ incr", incr)
    switch (action.type) {
        case "INCREMENT":
            return {
                // array
                // element: state[element]
                ...state,
                array: incr
            }
            break;
        case "REMOVE":
            return {
                ...state,
                array: filteredArray
            }
        default:
            break;
    }
    return state
}
