import { ADDCATEGORIES, ADDDATA, ADDTOCART } from './action'

let globalState: any = {
    cats: [],
    categories: []
}

export default function reducer(state = globalState, action: any) {
    switch (action.type) {
        case ADDCATEGORIES:
            return {
                ...state,
                categories: action.data
            }
        case ADDDATA:
            return {
                ...state,
                cats: action.catData
            }
        default:
            return state;
    }
}
