}

export default function reducer(state = globalState, action: any) {
    console.log(action)
    let table = [...state.array]
    let filteredArray = table.filter(a => a.id !== action.id)
    let dublicate: any = Object.assign({}, state)
    switch (action.type) {
        case "INCREMENT":
            console.log(state);

            return {
                // array
