import React from 'react'
import styled from 'styled-components'
import { useDispatch } from 'react-redux'

export default function Card(props: any) {
    console.log("ðŸš€ ~ file: index.tsx ~ line 6 ~ Card ~ props", props)
    const { name, price, description, image, count } = props
    return (
        <Container>
            <CardHeader>
                <h2>{name}</h2>
                <StyledImage>
                    <img src={image} alt="" />
                </StyledImage>
                <p>{description}</p>
                <p>{price}</p>
                <p>{count} pieces</p>
            </CardHeader>
            <ButtonContainer>
                <Button disabled background="red">-</Button>
                <Button background="green">+</Button>
                <Button width={110}>Buy</Button>
            </ButtonContainer>
        </Container>
    )
}

const Container = styled.div`
    background-color: aliceblue;
    border-radius: 23px;
    padding: 20px;
    margin: 0 40px 40px;
    height: 480px;
    width: 280px;
`

const CardHeader = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
`

const StyledImage = styled.div`
    width: 100%;
    height: 250px;
    img {
        width: 100%;
        height: 100%;
    }
`

const ButtonContainer = styled.div`
    display: flex;
    width: 100%;
    justify-content: space-between;
    margin-top: 20px;
`

const Button = styled.button<{ background?: string; width?: number }>`
    width: ${({ width }) => width ? width : 60}px;
    height: 60px;
    border-radius: 10px;
    background: ${({ background }) => background ? background : "rgba(0, 0, 0, 0) linear-gradient(270deg, rgb(250, 188, 126) 0%, rgb(255, 150, 45) 100%) repeat scroll right bottom"};
    cursor: pointer;
    font-size: 20px;
    :disabled {
        cursor: not-allowed;
        background: rgba(172, 163, 163,0.5);
    }
`
