import { useSelector, useDispatch } from 'react-redux'
import styled from 'styled-components'
import Cat, { Button } from '../cat'
import Categories from '../categories'
import { useEffect, useState } from 'react'
import { ADDDATA, ADDCATEGORIES } from "../../store/action"
export default function Home() {
    const state = useSelector((store: any) => store.cats)
    console.log("ðŸš€ ~ file: index.tsx ~ line 9 ~ Home ~ state", state)
    const dispatch = useDispatch()
    const [data, setData] = useState()
    const [catData, setCatData] = useState()
    const [pageNumber, setPageNumber] = useState(10)
    const categories = useSelector((store: any) => store.categories)
    console.log("ðŸš€ ~ file: index.tsx ~ line 8 ~ Cart ~ state", state)
    useEffect(() => {
        fetch("https://api.thecatapi.com/v1/categories")
            .then((response: any) => {
                if (response.ok) {
                    return response.json()
                }
                throw response
            })
            .then((data: any) => {
                setData(data)
            })
            .catch(error => {
                console.log("Throw new error", error);
            })
        fetch(`https://api.thecatapi.com/v1/images/search?limit=${pageNumber}&page=1&category_ids=1`)
            .then((response: any) => {
                if (response.ok) {
                    return response.json()
                }
                throw response
            })
            .then((data: any) => {
                setCatData(data)
            })
            .catch(error => {
                console.log("Throw new error", error);
            })
    }, [pageNumber])
    dispatch({ type: ADDCATEGORIES, data })
    dispatch({ type: ADDDATA, catData })
    const click = () => {
        setPageNumber(initial => initial + 10)
        console.log("ðŸš€ ~ file: index.tsx ~ line 49 ~ click ~ pageNumber", pageNumber)
    }
    return (
        <>
            <Container>
                {categories?.map((item: any) => <Categories key={item.id} name={item.name} />)}
                {
                    state?.map((a: any) => (
                        <Cat key={a.id} id={a.id} image={a.url} />
                    ))
                }
            </Container>
            <Button onClick={click} width={200}>Load more...</Button>
        </>
    )
}

const Container = styled.div`
    display: flex;
    flex-wrap: wrap;
    justify-content: left;
    align-items: center;
    width: 100%;
    max-width: 1324px;
    padding: 40px 0;
    margin: auto;
`
