import styled from "styled-components";
import { useSelector } from 'react-redux'
import { StyledContainer } from "../card";
export default function Cart() {
	const cart = useSelector((store:any) => store.cart)
	return(
		<Container>
			<StyledIcon>
				<i className="fa fa-shopping-cart" style={{fontSize:"36px"}}></i>
			</StyledIcon>
			<StyledCart>
				<StyledContainer>
					<CardHeader>
						<h2>{cart.name}</h2>
						<StyledImage>
							<img src={cart.image} alt="" />
						</StyledImage>
						<p>{cart.description}</p>
						<p>${cart.price}</p>
						<p>{cart.count} pieces</p>
					</CardHeader>
					<ButtonContainer>
						<Button disabled background="red" onClick={() => dispatch({ type: "DECREMENT", id })}>-</Button>
                <Button background="green" onClick={() => dispatch({ type: "INCREMENT", id })}>+</Button>
						<Button width={130} onClick={() => dispatch({ type: ADDTOCART, delivery })}>Add to Card</Button>
					</ButtonContainer>
				</StyledContainer>
			</StyledCart>
		</Container>
	)
}

const StyledCart = styled.div`
	position: absolute;
	width: 0;
	left: 150%;
	right: 0;
	height: 100vh;
	top: 0;
	bottom: 0;
`

const Container = styled.div`
	position: absolute;
	background-color: aliceblue;
	right: 0;
	top: 100px;
`

const StyledIcon = styled.button`
	border-radius: 10px;
	width: 40px;
	height: 40px;
	border: rgb(255, 150, 45);
	cursor: pointer;
	right: 0;
	display: flex;
	align-items: right;
	justify-content: center;
`
