    ],
    cart: [],
    categories: []
}

export default function reducer(state = globalState, action: any) {
    let table = [...state.array]
    let cart = [...state.cart]
    let filteredArray = table.filter(a => a.id !== action.id)
    switch (action.type) {
        case "INCREMENT":
            console.log(action);
            const foundTable = table.find(item => (item.id) === parseInt(action.id))
            foundTable && (foundTable.count = foundTable.count + 1)
            console.log("ðŸš€ ~ file: reducer.ts ~ line 90 ~ reducer ~ foundTable", foundTable)
            return {
                // array
