                ...state,
                array: table
            }
        case "REMOVE":
            return {
                ...state,
                array: filteredArray
            }
        case ADDCATEGORIES:
            console.log(action)
            return {
                ...state,
                categories: action.data
            }
        case ADDTOCART:
            // let foundedObject:any = table.find(item => item.id === action.delivery.id)
            let foundedObject = action.delivery
            const objectExist = cart.some(item => item.id === foundedObject.id)
            if (cart.length === 0) {
                cart.push(foundedObject)
            } else {
                cart.map(item => {
                    if (objectExist) {
                        item.count = item.count + 1
                        console.log("if")
                    } else {
                        cart.push(foundedObject)
                        console.log("else")
                    }
                })
            }
            console.log(cart);

            return {
